import { useState } from 'react';
import './App.css';
import Header from './componentes/Header'
import Stats from './componentes/Stats'
import axios from 'axios';

function App() {

  const[playlistLink, setPlaylistLink] = useState("")

  let changePlaylistLink = (playlistLink) => {
    setPlaylistLink(playlistLink)
  }

  const [data, setData] = useState(null);
  const [error, setError] = useState("");
  const [loaded, setLoaded] = useState(false);

  useEffect(() => {
    // Esta função será executada quando playlistLink for alterado
    if (playlistLink) {
      fetchPlaylist();
    }
  }, [playlistLink]); // Só reagir às mudanças em playlistLink

  const fetchPlaylist = () => {

    console.log('fez fetch')

    var test_data = {
      "playlist_link": playlistLink,
      "access_token": "BQAzK6psaX6e5cm98kdpGKqKEDA7vQLfh5IBzku5bHaB3ZkHBFTZyfB8Dqq_wbuZelkpQaDFCcrRFnOlsdeH70UoaLh_8NOfRAckb_lueOjijA6bKzA"
    }

    axios
      .post("http://localhost:8000/playlist_get_info/", test_data)
      .then((response) => setData(response.data))
      .catch((error) => setError(error.message))
      .finally(() => setLoaded(true));
  };

  return (
    <div>
      <Header changePlaylistLink={changePlaylistLink} playlistLink={playlistLink} fetchPlaylist={fetchPlaylist}/>
      <Stats data={data} error={error} loaded={loaded}/>
    </div>
  );

}

export default App;
