          .then((response) => setData(response.data))
          .catch((error) => setError(error.message))
          .finally(() => setLoaded(true));
    }, []);

    const getInfoRight = () => {

        const topArtists = Object.keys(data['top artists']['Artist']).map((key) => ({
            name: jsonData['top artists']['Artist'][key],
            appearances: jsonData['top artists']['Appearances'][key],
            percentage: jsonData['top artists']['%'][key],
        }));

        const topAlbums = Object.keys(data['top_albums']['Album']).map((key) => ({
            name: jsonData['top_albums']['Album'][key],
            appearances: jsonData['top_albums']['appearances'][key],
            percentage: jsonData['top_albums']['%'][key],
        }));


