import React from 'react'

import '../styles/Header.css'

import github from '../static/github.png'

const Header = (props) => {

  return (
    <div className='nav'>
        <h1 className='nav--h1'>
          Spotipy</h1>

        <div className='middle-section'>
          <textarea onChange={(e) => props.changePlaylistLink(e.target.value)} 
                    value = {props.playlistLink} 
                    className='middle-section--textarea' 
                    rows={1}
                    placeholder='https://open.spotify.com/playlist/<playlist_link>?si=<playlist_code>'
          >
          </textarea>

          <button onClick={props.getPlaylistStats}
                  disabled={!props.buttonCanBeClicked}
                  className={`middle-section--button ${props.buttonCanBeClicked ? 'enabled' : 'disabled'}`}>
            Get Stats
          </button>
        </div>

        <a href='https://github.com/victorleaoo/Spotify-Playlists-WebApp' target="_blank" rel="noreferrer" >
          <img src={github} alt='Github Logo' className='nav--image'></img>
        </a>
    </div>
  )

}

export default Header
