import React, { useEffect, useState } from 'react'
import axios from 'axios';

const Stats = () => {
    // Enquanto está carregando
    if (loaded === false){
        return "Carregando..."
    } else{
        // Com erro
        if (error){
            return error
        }

        const topArtists = Object.keys(props.data['top artists']['Artist']).map((key) => ({
            name: props.data['top artists']['Artist'][key],
            appearances: props.data['top artists']['Appearances'][key],
            percentage: props.data['top artists']['%'][key],
            })
        );

        const topAlbums = Object.keys(props.data['top_albums']['Album']).map((key) => ({
            name: props.data['top_albums']['Album'][key],
            appearances: props.data['top_albums']['appearances'][key],
            percentage: props.data['top_albums']['%'][key],
            })
        );

        // Sem erro
        return (
            <div>

                <h1>Top Artists</h1>
                <ul>
                    {topArtists.map((artist, index) => (
                    <li key={index}>
                        {artist.name} - Appearances: {artist.appearances}, Percentage: {artist.percentage}
                    </li>
                    ))}
                </ul>

                <h1>Top Albums</h1>
                <ul>
                    {topAlbums.map((album, index) => (
                    <li key={index}>
                        {album.name} - Appearances: {album.appearances}, Percentage: {album.percentage}
                    </li>
                    ))}
                </ul>

                <h1>Most Popular</h1>
                <ul>
                    <li>
                        Track Name: {props.data['most_popular_track']['Track Name']}
                    </li>
                    <li>
                        Track Artists: {props.data['most_popular_track']['Track Artists']}
                    </li>
                    <li>
                        Album Name: {props.data['most_popular_track']['Album Name']}
                    </li>
                    <li>
                        Track Popularity: {props.data['most_popular_track']['Track Popularity']}
                    </li>
                    <li>
                        Track Duration: {props.data['most_popular_track']['Track Duration']}
                    </li>
                </ul>

                <h1>Longest</h1>
                <ul>
                    <li>
                        Track Name: {props.data['longest_track']['Track Name']}
                    </li>
                    <li>
                        Track Artists: {props.data['longest_track']['Track Artists']}
                    </li>
                    <li>
                        Album Name: {props.data['longest_track']['Album Name']}
                    </li>
                    <li>
                        Track Popularity: {props.data['longest_track']['Track Popularity']}
                    </li>
                    <li>
                        Track Duration: {props.data['longest_track']['Track Duration']}
                    </li>
                </ul>

                <h1>Shortest</h1>
                <ul>
                    <li>
                        Track Name: {props.data['shortest_track']['Track Name']}
                    </li>
                    <li>
                        Track Artists: {props.data['shortest_track']['Track Artists']}
                    </li>
                    <li>
                        Album Name: {props.data['shortest_track']['Album Name']}
                    </li>
                    <li>
                        Track Popularity: {props.data['shortest_track']['Track Popularity']}
                    </li>
                    <li>
                        Track Duration: {props.data['shortest_track']['Track Duration']}
                    </li>
                </ul>

            </div>
        )
    }
}

export default Stats
