import { useState } from 'react';
import './App.css';
import Header from './componentes/Header'
import Stats from './componentes/Stats'
import axios from 'axios';

function App() {

  const [playlistLink, setPlaylistLink] = useState("");
  const [buttonCanBeClicked, setbuttonCanBeClicked] = useState(false);

  let changePlaylistLink = (playlistLink) => {
    setPlaylistLink(playlistLink)

    const regex = /^https:\/\/open\.spotify\.com\/playlist\/[A-Za-z0-9_-]+(\?si=[A-Za-z0-9_-]+)*$/;
    setbuttonCanBeClicked()
  }

  const [data, setData] = useState(null);
  const [error, setError] = useState("");
  const [loaded, setLoaded] = useState(false);

  let getPlaylistStats = () => {
    var test_data = {
      "playlist_link": playlistLink,
      "access_token": "BQCelWtU-VJ-29MAwoG_3QkxeM4AVcBQ4reFEo60GRvbGrAVB8tdA3m_fyle6Wyuy-Fh7yT3jAUu0EcK70rvamlrWYKxBAN-SZRSDaugnYMzVIA7Poo"
    }

    axios
      .post("http://localhost:8000/playlist_get_info/", test_data)
      .then((response) => setData(response.data))
      .catch((error) => setError(error.message))
      .finally(() => setLoaded(true));
  }

  return (
    <div>
      <Header changePlaylistLink={changePlaylistLink} playlistLink={playlistLink} getPlaylistStats={getPlaylistStats}/>
      <Stats key={loaded} data={data} error={error} loaded={loaded}/>
    </div>
  );

}

export default App;
