import React, { useEffect, useState } from 'react'
import axios from 'axios';

const Stats = () => {

    var test_data = {
        "playlist_link": "https://open.spotify.com/playlist/0XipLi1tlMf0Ey0p1TKnLi?si=53512ab488414b26",
        "access_token": "BQAMSP3xbYO9yCWLt7YpXQQqpMzKJhIdJSlF_OhloyRPtcTmx_kPjbjzpoAvE6_MCVTjIuUfMWzWbTCvttRFnz6qgHPhHkPy-vsFpVGVHWY_HghuhDE"
    }

    const [data, setData] = useState(null);
    const [error, setError] = useState("");
    const [loaded, setLoaded] = useState("");

    useEffect(() => {
        axios
          .post("http://localhost:8000/playlist_get_info/", test_data)
          .then((response) => setData(response.data))
          .catch((error) => setError(error.message))
          .finally(() => setLoaded(true));
    }, []);

    console.log(data)

    if (loaded === false){
        return "Carregando..."
    } else{
        if (data === null){
            return ""
        }
        return data['top artists']['Artist']
    }

    return (
        <div>
            <h1>Stats/Charts</h1>
            <p>{loaded ? "Deu Certo o carregamento" : "Deu Problema o carregamento"}</p>
            <p>{error ? error : "Sem erro"}</p>

        </div>
    )
}

export default Stats
