import React, { useEffect, useState } from 'react'
import axios from 'axios';

const Stats = () => {

    var test_data = {
        "playlist_link": "https://open.spotify.com/playlist/0XipLi1tlMf0Ey0p1TKnLi?si=53512ab488414b26",
        "access_token": "BQC0A4_D5EnxagwM9rmZI1siV4fELSiv31sQl4INMhgO-obqCDSNuhW5GHp877v2gb6JPRxv3Po3sSlKvbuYeXY_08-I7B5thCBQ7cm0X0mWcdJkdW4"
    }

    const [data, setData] = useState(null);
    const [error, setError] = useState("");
    const [loaded, setLoaded] = useState(false);

    useEffect(() => {
        axios
          .post("http://localhost:8000/playlist_get_info/", test_data)
          .then((response) => setData(response.data))
          .catch((error) => setError(error.message))
          .finally(() => setLoaded(true));
    }, []);

    const getInfoRight = () => {

        const topArtists = Object.keys(data['top artists']['Artist']).map((key) => ({
            name: data['top artists']['Artist'][key],
            appearances: data['top artists']['Appearances'][key],
            percentage: data['top artists']['%'][key],
        }));

        const topAlbums = Object.keys(data['top_albums']['Album']).map((key) => ({
            name: data['top_albums']['Album'][key],
            appearances: data['top_albums']['appearances'][key],
            percentage: data['top_albums']['%'][key],
        }));

        return topArtists, topAlbums
    }

    if (loaded === false){
        return "Carregando..."
    } else{
        if (data === null){
            return "sem dados"
        }

        topArtists, topgetInfoRight()

        return (
            <div>
                <h1>Top Artists</h1>
                <ul>
                    {topArtists.map((artist, index) => (
                    <li key={index}>
                        {artist.name} - Appearances: {artist.appearances}, Percentage: {artist.percentage}
                    </li>
                    ))}
                </ul>

                <h1>Top Albums</h1>
                <ul>
                    {topAlbums.map((album, index) => (
                    <li key={index}>
                        {album.name} - Appearances: {album.appearances}, Percentage: {album.percentage}
                    </li>
                    ))}
                </ul>
            </div>
        )
    }
}

export default Stats
