import { useState } from 'react';
import axios from 'axios';

import Header from './componentes/Header'
import Stats from './componentes/Stats'

import './App.css';

let fetchPlaylistData = 0;

function App() {

  // States
  const [playlistLink, setPlaylistLink] = useState(""); // Playlist Link
  const [buttonCanBeClicked, setButtonCanBeClicked] = useState(false); // Enable/Disable "Get Stats" Button

  const [token, setToken] = useState(""); // Spotify Auth Token
  const [data, setData] = useState(null);
  const [error, setError] = useState("");
  const [loaded, setLoaded] = useState(false);
  const [loading, setLoading] = useState(false);

  let changePlaylistLink = (playlistLink) => {
    setPlaylistLink(playlistLink)

    const regex = /^https:\/\/open\.spotify\.com\/playlist\/[A-Za-z0-9_-]+(\?si=[A-Za-z0-9_-]+)*$/;

    const regexCorrect = regex.test(playlistLink)
    setButtonCanBeClicked(regexCorrect)
  }

  let getPlaylistStats = () => {
    const hash = window.location.hash;
    let token = window.localStorage.getItem("token");
    let tokenExpiration = window.localStorage.getItem("tokenExpiration");

    if (!token && hash) {
        token = hash.substring(1).split("&").find(elem => elem.startsWith("access_token")).split("=")[1];

        // Obtenha o tempo de expiração em segundos (suponhamos que o serviço forneça esse valor)
        const expiresIn = hash.substring(1).split("&").find(elem => elem.startsWith("expires_in")).split("=")[1];

        // Calcule a data de expiração em milissegundos a partir do tempo de expiração
        const expirationTime = Date.now() + (parseInt(expiresIn) * 1000);

        window.location.hash = "";
        window.localStorage.setItem("token", token);
        window.localStorage.setItem("tokenExpiration", expirationTime);
    }

    // Verifique se o token está expirado e remova-o do localStorage, se necessário
    if (token && tokenExpiration) {
        const currentTime = Date.now();
        if (currentTime >= parseInt(tokenExpiration)) {
            window.localStorage.removeItem("token");
            window.localStorage.removeItem("tokenExpiration");
            token = ""; // Token expirado, defina como null ou trate de acordo com sua lógica
        }
    }

    setToken(token)

    if (fetchPlaylistData > 0) {
      setLoaded(false);
    }
    setLoading(true);

    var test_data = {
      "playlist_link": playlistLink,
      "access_token": token,
    }

    axios
      .post("http://localhost:8000/playlist_get_info/", test_data)
      .then((response) => setData(response.data))
      .catch((error) => setError(error.message))
      .finally(() => {
        setLoading(false)
        setLoaded(true)
        fetchPlaylistData = 1;
      });
  }

  return (
    <div>
      <Header changePlaylistLink={changePlaylistLink} 
              playlistLink={playlistLink} 
              getPlaylistStats={getPlaylistStats}
              buttonCanBeClicked={buttonCanBeClicked}
      />
      <Stats key={loaded} 
             data={data} 
             error={error} 
             loaded={loaded} 
             loading={loading}
      />
    </div>
  );

}

export default App;
